// src/components/RealizarPedido.js
import React, { useState, useEffect } from 'react';
import './RealizarPedido.css';
// Si tienes un componente para un modal de confirmación, impórtalo aquí
// import ConfirmacionPedidoModal from './ConfirmacionPedidoModal';

function RealizarPedido() {
    // Estado para los datos del formulario
    const [formData, setFormData] = useState({
        nombre: '',
        telefono: '',
        producto: '',
        ingredientes: [], // Para guardar los ingredientes extras del elote
        cantidad: 1,
        notas: '',
        metodoEntrega: 'recoger', // 'recoger' o 'domicilio'
        direccion: '', // Solo relevante si es a domicilio
    });

    // Estado para controlar errores de validación
    const [errors, setErrors] = useState({});

    // Opciones de productos disponibles (pueden venir de una API o lista más grande)
    const productosDisponibles = [
        'Russa', 'Fruta', 'Elote en vaso', 'Tosticeviche', 'Tostielote', 'Boyas',
        'Tostada', 'Litro Ceviche', 'Chilidog'
    ];

    // Opciones de ingredientes extra para elote (ejemplo)
    const ingredientesElote = [
        'Queso Cotija', 'Mayonesa', 'Chili en polvo', 'Limón', 'Crema', 'Mantequilla'
    ];

    // Estado para el ingrediente actualmente seleccionado en el dropdown
    const [selectedIngrediente, setSelectedIngrediente] = useState('');

    // Manejador genérico para cambios en los inputs del formulario
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));

        // Limpia errores relacionados con el campo que se está modificando
        setErrors(prev => ({ ...prev, [name]: '' }));

        // Si el producto cambia y no es "Elote en vaso", limpia los ingredientes
        if (name === 'producto' && value !== 'Elote en vaso') {
            setFormData(prev => ({ ...prev, ingredientes: [] }));
            setSelectedIngrediente(''); // También limpia el selector de ingredientes
        }
    };

    // Manejador para añadir un ingrediente al elote
    const handleAddIngrediente = () => {
        if (selectedIngrediente && !formData.ingredientes.includes(selectedIngrediente)) {
            setFormData(prev => ({
                ...prev,
                ingredientes: [...prev.ingredientes, selectedIngrediente]
            }));
            setSelectedIngrediente(''); // Limpia el selector después de añadir
            setErrors(prev => ({ ...prev, ingredientes: '' })); // Limpia error si existía
        } else if (formData.ingredientes.includes(selectedIngrediente)) {
            // Opcional: feedback si el ingrediente ya fue añadido
            alert('Este ingrediente ya ha sido añadido.');
        }
    };

    // Manejador para eliminar un ingrediente del elote
    const handleRemoveIngrediente = (ingredienteToRemove) => {
        setFormData(prev => ({
            ...prev,
            ingredientes: prev.ingredientes.filter(ing => ing !== ingredienteToRemove)
        }));
    };

    // Función de validación del formulario
    const validateForm = () => {
        let newErrors = {};
        let isValid = true;

        if (!formData.nombre.trim()) {
            newErrors.nombre = 'El nombre es requerido.';
            isValid = false;
        }

        if (!formData.telefono.trim()) {
            newErrors.telefono = 'El teléfono es requerido.';
            isValid = false;
        } else if (!/^\d{10}$/.test(formData.telefono.trim())) { // Asume un teléfono de 10 dígitos
            newErrors.telefono = 'El teléfono debe tener 10 dígitos.';
            isValid = false;
        }

        if (!formData.producto) {
            newErrors.producto = 'Selecciona un producto.';
            isValid = false;
        }

        if (formData.metodoEntrega === 'domicilio' && !formData.direccion.trim()) {
            newErrors.direccion = 'La dirección es requerida para el servicio a domicilio.';
            isValid = false;
        }

        setErrors(newErrors);
        return isValid;
    };

    // Manejador para enviar el formulario
    const handleSubmit = (e) => {
        e.preventDefault();

        if (validateForm()) {
            // Aquí iría la lógica para enviar el pedido a un backend o servicio.
            // Por ahora, solo lo mostraremos en consola y un alert.
            console.log("Pedido Enviado:", formData);
            alert('¡Tu pedido ha sido enviado con éxito! Nos pondremos en contacto contigo pronto.');

            // Opcional: Reiniciar el formulario después de un envío exitoso
            setFormData({
                nombre: '',
                telefono: '',
                producto: '',
                ingredientes: [],
                cantidad: 1,
                notas: '',
                metodoEntrega: 'recoger',
                direccion: '',
            });
            setErrors({}); // Limpiar errores
            setSelectedIngrediente(''); // Limpiar selector de ingrediente
        } else {
            alert('Por favor, completa todos los campos requeridos y corrige los errores.');
            // Opcional: Scroll to the first error
            const firstErrorField = Object.keys(errors)[0];
            if (firstErrorField) {
                document.getElementById(firstErrorField)?.focus();
            }
        }
    };

    // Lógica para deshabilitar el botón de añadir ingrediente
    const isAddIngredienteDisabled = !selectedIngrediente || formData.producto !== 'Elote en vaso';

    // Lógica para deshabilitar el botón de enviar pedido
    // Este useEffect asegura que el estado de 'disabled' del botón se actualice con cada cambio de formData.
    const [isSubmitDisabled, setIsSubmitDisabled] = useState(true);

    useEffect(() => {
        setIsSubmitDisabled(!validateForm());
    }, [formData]); // Re-evaluar cuando formData cambie

    return (
        <div className="seccion-realizar-pedido">
            <h1 className="titulo-pedido">Realiza tu Pedido</h1>
            <p className="subtitulo-pedido">
                ¡Es fácil y rápido! Cuéntanos qué se te antoja de Kayak Snack.
                Si tienes dudas o necesitas un pedido más complejo, contáctanos directamente.
            </p>
            {/* Línea decorativa amarilla similar a las otras secciones */}
            <hr className="linea-decorativa-amarilla" />

            <form onSubmit={handleSubmit} className="contenedor-formulario-pedido">
                {/* Campo Nombre */}
                <div className="campo-grupo">
                    <label htmlFor="nombre" className="etiqueta-campo">Tu Nombre:</label>
                    <input
                        type="text"
                        id="nombre"
                        name="nombre"
                        value={formData.nombre}
                        onChange={handleChange}
                        className={`input-text ${errors.nombre ? 'input-error' : ''}`}
                        placeholder="Escribe tu nombre"
                        aria-invalid={errors.nombre ? "true" : "false"}
                        aria-describedby="error-nombre"
                    />
                    {errors.nombre && <p id="error-nombre" className="mensaje-error">{errors.nombre}</p>}
                </div>

                {/* Campo Teléfono */}
                <div className="campo-grupo">
                    <label htmlFor="telefono" className="etiqueta-campo">Número de Teléfono:</label>
                    <input
                        type="tel" // 'tel' para números de teléfono
                        id="telefono"
                        name="telefono"
                        value={formData.telefono}
                        onChange={handleChange}
                        className={`input-text ${errors.telefono ? 'input-error' : ''}`}
                        placeholder="Ej: 8112345678"
                        aria-invalid={errors.telefono ? "true" : "false"}
                        aria-describedby="error-telefono"
                    />
                    {errors.telefono && <p id="error-telefono" className="mensaje-error">{errors.telefono}</p>}
                </div>

                {/* Campo Producto */}
                <div className="campo-grupo">
                    <label htmlFor="producto" className="etiqueta-campo">Selecciona tu antojo:</label>
                    <select
                        id="producto"
                        name="producto"
                        value={formData.producto}
                        onChange={handleChange}
                        className={`input-select ${errors.producto ? 'input-error' : ''}`}
                        aria-invalid={errors.producto ? "true" : "false"}
                        aria-describedby="error-producto"
                    >
                        <option value="">-- Elige un producto --</option>
                        {productosDisponibles.map(producto => (
                            <option key={producto} value={producto}>{producto}</option>
                        ))}
                    </select>
                    {errors.producto && <p id="error-producto" className="mensaje-error">{errors.producto}</p>}
                </div>

                {/* Campo Ingredientes Extra (solo si el producto es "Elote en vaso") */}
                {formData.producto === 'Elote en vaso' && (
                    <div className="campo-grupo">
                        <label htmlFor="ingredienteExtra" className="etiqueta-campo">Ingredientes Extra para tu Elote:</label>
                        <div className="input-con-boton">
                            <select
                                id="ingredienteExtra"
                                name="ingredienteExtra"
                                value={selectedIngrediente}
                                onChange={(e) => setSelectedIngrediente(e.target.value)}
                                className="input-select"
                            >
                                <option value="">-- Selecciona un ingrediente --</option>
                                {ingredientesElote.map(ingrediente => (
                                    <option
                                        key={ingrediente}
                                        value={ingrediente}
                                        disabled={formData.ingredientes.includes(ingrediente)}
                                    >
                                        {ingrediente}
                                    </option>
                                ))}
                            </select>
                            <button
                                type="button"
                                onClick={handleAddIngrediente}
                                className="boton-añadir"
                                disabled={isAddIngredienteDisabled}
                            >
                                Añadir
                            </button>
                        </div>
                        {formData.ingredientes.length > 0 && (
                            <div className="ingredientes-agregados">
                                <p className="titulo-agregados">Agregados:</p>
                                <ul className="lista-agregados">
                                    {formData.ingredientes.map(ingrediente => (
                                        <li key={ingrediente}>
                                            {ingrediente}
                                            <button
                                                type="button"
                                                onClick={() => handleRemoveIngrediente(ingrediente)}
                                                className="boton-eliminar-ingrediente"
                                                title="Eliminar ingrediente"
                                            >
                                                &times; {/* Símbolo de cruz para eliminar */}
                                            </button>
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        )}
                    </div>
                )}

                {/* Campo Cantidad */}
                <div className="campo-grupo">
                    <label htmlFor="cantidad" className="etiqueta-campo">Cantidad:</label>
                    <input
                        type="number"
                        id="cantidad"
                        name="cantidad"
                        value={formData.cantidad}
                        onChange={handleChange}
                        className="input-text"
                        min="1"
                        required
                    />
                </div>

                {/* Campo Notas Adicionales */}
                <div className="campo-grupo">
                    <label htmlFor="notas" className="etiqueta-campo">Notas adicionales (opcional):</label>
                    <textarea
                        id="notas"
                        name="notas"
                        value={formData.notas}
                        onChange={handleChange}
                        className="input-text"
                        rows="4"
                        placeholder="Ej: sin picante, con mucha crema, etc."
                    ></textarea>
                </div>

                {/* Campo Método de Entrega */}
                <div className="campo-grupo">
                    <label htmlFor="metodoEntrega" className="etiqueta-campo">Método de entrega:</label>
                    <select
                        id="metodoEntrega"
                        name="metodoEntrega"
                        value={formData.metodoEntrega}
                        onChange={handleChange}
                        className="input-select"
                    >
                        <option value="recoger">Recoger en local</option>
                        <option value="domicilio">Servicio a domicilio</option>
                    </select>
                </div>

                {/* Campo Dirección (solo si el método de entrega es "domicilio") */}
                {formData.metodoEntrega === 'domicilio' && (
                    <div className="campo-grupo">
                        <label htmlFor="direccion" className="etiqueta-campo">Dirección de entrega:</label>
                        <input
                            type="text"
                            id="direccion"
                            name="direccion"
                            value={formData.direccion}
                            onChange={handleChange}
                            className={`input-text ${errors.direccion ? 'input-error' : ''}`}
                            placeholder="Calle, número, colonia, código postal"
                            aria-invalid={errors.direccion ? "true" : "false"}
                            aria-describedby="error-direccion"
                        />
                        {errors.direccion && <p id="error-direccion" className="mensaje-error">{errors.direccion}</p>}
                    </div>
                )}

                {/* Botón para completar el pedido */}
                <button
                    type="submit"
                    className="boton-completar-pedido"
                    disabled={isSubmitDisabled}
                >
                    Enviar Pedido
                </button>
            </form>

            <p className="info-telefono">
                Si prefieres, también puedes hacer tu pedido llamando al: <strong>81 1234 5678</strong>
            </p>
        </div>
    );
}

export default RealizarPedido;
