// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import './App.css'; // Estilos globales para App.js
import RealizarPedido from './components/RealizarPedido';
// Si tienes una página de inicio, puedes importarla aquí
// import HomePage from './components/HomePage';
// Si tienes una página de contacto
// import ContactPage from './components/ContactPage';
// Si tienes una página de menú (distinta de RealizarPedido)
// import MenuPage from './components/MenuPage';

// PedidoConfirmacion es un modal, por lo general no se renderiza como una ruta de página
// Se renderiza condicionalmente dentro de otro componente (ej. RealizarPedido o Carrito)
// import PedidoConfirmacion from './components/PedidoConfirmacion';


function App() {
    // Aquí puedes manejar estados globales si los necesitas,
    // por ejemplo, para un carrito de compras o datos del usuario logueado.
    const [currentOrder, setCurrentOrder] = useState(null); // Para mostrar detalles de un pedido actual

    // Ejemplo de un supuesto modal de confirmación
    // Normalmente, PedidoConfirmacion se manejaría con estado dentro de RealizarPedido
    // o un componente de carrito, no directamente en App.js como una ruta.
    // Lo incluyo solo para ilustrar su uso si fuera necesario.

    // const [showConfirmationModal, setShowConfirmationModal] = useState(false);
    // const [confirmedOrderData, setConfirmedOrderData] = useState(null);

    // const handleConfirmOrder = (orderData) => {
    //     setConfirmedOrderData(orderData);
    //     setShowConfirmationModal(true);
    // };

    // const handleCloseConfirmation = () => {
    //     setShowConfirmationModal(false);
    //     setConfirmedOrderData(null);
    // };

    return (
        <Router>
            <div className="App">
                {/* Header (opcional) - Podrías tener un componente Header separado */}
                <header className="App-header">
                    <nav>
                        <ul className="nav-list">
                            <li><Link to="/" className="nav-link">Inicio</Link></li>
                            <li><Link to="/realizar-pedido" className="nav-link">Realizar Pedido</Link></li>
                            {/* Agrega más enlaces si tienes otras páginas */}
                            {/* <li><Link to="/menu" className="nav-link">Nuestro Menú</Link></li> */}
                            {/* <li><Link to="/contacto" className="nav-link">Contacto</Link></li> */}
                        </ul>
                    </nav>
                </header>

                {/* Contenido principal que cambia según la ruta */}
                <main className="App-main">
                    <Routes>
                        {/* Ruta para la página de inicio (puedes reemplazar `<h1>` con tu componente HomePage) */}
                        <Route path="/" element={
                            <div className="home-page-content">
                                <h1>¡Bienvenido a Kayak Snack!</h1>
                                <p>Tu destino para los antojos más deliciosos. Haz tu pedido ahora.</p>
                                {/* Puedes añadir una imagen o más texto aquí */}
                                <Link to="/realizar-pedido" className="start-order-button">¡Haz tu Pedido Ahora!</Link>
                            </div>
                        } />

                        {/* Ruta para el componente RealizarPedido */}
                        <Route path="/realizar-pedido" element={<RealizarPedido />} />

                        {/* Agrega rutas para otros componentes si los tienes */}
                        {/* <Route path="/menu" element={<MenuPage />} /> */}
                        {/* <Route path="/contacto" element={<ContactPage />} /> */}

                        {/* Ruta de fallback para páginas no encontradas (404) */}
                        <Route path="*" element={
                            <div className="not-found-page">
                                <h2>404 - Página No Encontrada</h2>
                                <p>Lo sentimos, la página que buscas no existe.</p>
                                <Link to="/">Volver al inicio</Link>
                            </div>
                        } />
                    </Routes>
                </main>

                {/* Footer (opcional) - Podrías tener un componente Footer separado */}
                <footer className="App-footer">
                    <p>&copy; 2025 Kayak Snack. Todos los derechos reservados.</p>
                </footer>

                {/* El modal de confirmación no es una ruta, se renderiza condicionalmente */}
                {/* {showConfirmationModal && confirmedOrderData && (
                    <PedidoConfirmacion
                        pedido={confirmedOrderData}
                        onClose={handleCloseConfirmation}
                        onPedirMas={() => {
                            handleCloseConfirmation();
                            // Lógica para permitir al usuario añadir más productos
                            alert('Puedes añadir más productos!');
                        }}
                        onCancelar={handleCloseConfirmation} // Simple cancelación del modal
                        onTerminarPedido={() => {
                            alert('Pedido Terminado y en proceso!');
                            handleCloseConfirmation();
                            // Aquí enviarías el pedido final al backend
                        }}
                    />
                )} */}
            </div>
        </Router>
    );
}

export default App;
