// src/components/IngredienteInputs.js
import React from 'react';

const IngredienteInputs = ({
    label,
    placeholder,
    suggestions, // Array de strings con las sugerencias de ingredientes
    currentValue,
    onValueChange,
    onAddClick,
    isDisabled,
    errorMessage // Prop para el mensaje de error
}) => {
    // Genera un ID único para el datalist y el input
    const uniqueId = Math.random().toString(36).substring(2, 9);
    const dataListId = `suggestions-${uniqueId}`;
    const inputId = `ingrediente-input-${uniqueId}`;

    return (
        <div className="campo-grupo">
            <label htmlFor={inputId} className="etiqueta-campo">
                {label}
            </label>
            <div className="input-con-boton">
                <input
                    type="text"
                    id={inputId}
                    className={`input-text ${errorMessage ? 'input-error' : ''}`}
                    placeholder={placeholder}
                    value={currentValue}
                    onChange={(e) => onValueChange(e.target.value)}
                    onKeyPress={(e) => {
                        if (e.key === 'Enter') {
                            e.preventDefault(); // Evita que se envíe el formulario
                            onAddClick();
                        }
                    }}
                    list={dataListId} // Conecta el input con el datalist
                    aria-invalid={errorMessage ? "true" : "false"}
                    aria-describedby={errorMessage ? `error-${inputId}` : undefined}
                />
                <button
                    type="button"
                    onClick={onAddClick}
                    className="boton-añadir"
                    disabled={isDisabled}
                >
                    Añadir
                </button>
                {/* El DATALIST que muestra las opciones */}
                {/* Se renderiza solo si hay sugerencias */}
                {suggestions && suggestions.length > 0 && (
                    <datalist id={dataListId}>
                        {suggestions.map((ingrediente, index) => (
                            <option key={index} value={ingrediente} />
                        ))}
                    </datalist>
                )}
            </div>
            {errorMessage && <p id={`error-${inputId}`} className="mensaje-error">{errorMessage}</p>}
        </div>
    );
};

export default IngredienteInputs;
