import React, { useState, useRef, useEffect } from 'react';
import './IngredienteInputs.css'; // Crea este CSS también

function IngredienteInputs({
    label,
    placeholder,
    suggestions,
    currentValue,
    onValueChange,
    onAddClick,
    isDisabled,
    errorMessage // Para mostrar errores específicos del input si los hubiera
}) {
    const [showSuggestions, setShowSuggestions] = useState(false);
    const [filteredSuggestions, setFilteredSuggestions] = useState(suggestions);
    const inputRef = useRef(null);
    const dropdownRef = useRef(null);

    // Efecto para filtrar sugerencias cuando el valor del input o la lista de sugerencias cambie
    useEffect(() => {
        setFilteredSuggestions(
            suggestions.filter(sug =>
                sug.toLowerCase().includes(currentValue.toLowerCase())
            )
        );
    }, [currentValue, suggestions]);

    // Efecto para manejar clics fuera del componente y cerrar las sugerencias
    useEffect(() => {
        const handleClickOutside = (event) => {
            // Verifica si el clic fue fuera del input y del dropdown de sugerencias
            if (dropdownRef.current && !dropdownRef.current.contains(event.target) &&
                inputRef.current && !inputRef.current.contains(event.target)) {
                setShowSuggestions(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    const handleInputChange = (e) => {
        onValueChange(e.target.value);
        setShowSuggestions(true); // Mostrar sugerencias al escribir
    };

    const handleSuggestionClick = (suggestion) => {
        onValueChange(suggestion);
        setShowSuggestions(false); // Ocultar sugerencias al seleccionar
        inputRef.current.focus(); // Volver a enfocar el input para seguir añadiendo o pulsando enter
    };

    const handleInputFocus = () => {
        // Muestra sugerencias al enfocar si ya hay algo escrito o si el campo está vacío
        if (currentValue || suggestions.length > 0) {
            setShowSuggestions(true);
        }
    };

    const handleKeyDown = (e) => {
        if (e.key === 'Enter') {
            e.preventDefault(); // Prevenir submit de formulario
            if (showSuggestions && filteredSuggestions.length > 0) {
                // Si hay sugerencias y el menú está abierto, seleccionar la primera
                onValueChange(filteredSuggestions[0]);
                setShowSuggestions(false);
                inputRef.current.focus();
            } else {
                // Si no hay sugerencias o el menú está cerrado, intentar añadir
                onAddClick();
            }
        } else if (e.key === 'Escape') {
            setShowSuggestions(false);
            inputRef.current.blur();
        }
        // Puedes añadir lógica para navegar con flechas (Up/Down) aquí si lo deseas
    };

    return (
        <div className="ingrediente-inputs-container">
            <label htmlFor="ingredientInput" className="etiqueta-campo">{label}</label>
            <div className="input-con-boton">
                <div className="autocomplete-wrapper" ref={dropdownRef}>
                    <input
                        type="text"
                        id="ingredientInput"
                        name="ingredientInput"
                        value={currentValue}
                        onChange={handleInputChange}
                        onFocus={handleInputFocus}
                        onKeyDown={handleKeyDown}
                        className={`input-text ${errorMessage ? 'input-error' : ''}`}
                        placeholder={placeholder}
                        autoComplete="off" // Deshabilitar autocomplete del navegador
                        ref={inputRef}
                        aria-autocomplete="list"
                        aria-controls="suggestions-list"
                        aria-expanded={showSuggestions && filteredSuggestions.length > 0}
                    />
                    {showSuggestions && filteredSuggestions.length > 0 && (
                        <ul className="suggestions-list" id="suggestions-list">
                            {filteredSuggestions.map((suggestion) => (
                                <li
                                    key={suggestion}
                                    onClick={() => handleSuggestionClick(suggestion)}
                                    className="suggestion-item"
                                >
                                    {suggestion}
                                </li>
                            ))}
                        </ul>
                    )}
                </div>
                <button
                    type="button"
                    onClick={onAddClick}
                    className="boton-añadir"
                    disabled={isDisabled}
                >
                    Añadir
                </button>
            </div>
            {errorMessage && <p className="mensaje-error">{errorMessage}</p>}
        </div>
    );
}

export default IngredienteInputs;
