// src/components/RealizarPedido.js
import React, { useState } from 'react';
import './RealizarPedido.css'; // Importa el archivo CSS para este componente

function RealizarPedido() {
  const [selectedAntojo, setSelectedAntojo] = useState('');
  const [ingrediente, setIngrediente] = useState('');
  const [ingredientesList, setIngredientesList] = useState([]);

  const handleAntojoChange = (event) => {
    setSelectedAntojo(event.target.value);
  };

  const handleIngredienteChange = (event) => {
    setIngrediente(event.target.value);
  };

  const handleAddIngrediente = () => {
    if (ingrediente.trim() !== '') {
      setIngredientesList([...ingredientesList, ingrediente.trim()]);
      setIngrediente('');
    }
  };

  const handleCompletarPedido = () => {
    // Aquí puedes añadir la lógica para enviar el pedido
    // Por ejemplo, mostrar un mensaje de confirmación o navegar a otra página
    alert(`Pedido completado: ${selectedAntojo} con ingredientes: ${ingredientesList.join(', ')}`);
    // Opcional: resetear el formulario
    setSelectedAntojo('');
    setIngrediente('');
    setIngredientesList([]);
  };

  return (
    <div className="seccion-realizar-pedido">
      <h1 className="titulo-pedido">Haz tu Pedido</h1>
      <p className="subtitulo-pedido">Personaliza tu antojo y disfruta de los mejores snacks de Kayak Snack</p>

      <div className="contenedor-formulario-pedido">
        {/* Sección "Selecciona tu antojo" */}
        <div className="campo-grupo">
          <label htmlFor="antojo-select" className="etiqueta-campo">Selecciona tu antojo</label>
          <select
            id="antojo-select"
            className="input-select"
            value={selectedAntojo}
            onChange={handleAntojoChange}
          >
            <option value="">Selecciona una opción</option>
            <option value="Fruta Fresca">Fruta Fresca</option>
            <option value="Elote en vaso">Elote en vaso</option>
            <option value="Tosticeviche">Tosticeviche</option>
            <option value="Tostielote">Tostielote</option>
            <option value="Litro Ceviche">Litro Ceviche</option>
            <option value="Chilidog">Chilidog</option>
            {/* Añade más opciones según tus productos */}
          </select>
        </div>

        {/* Sección "Añadir ingredientes (opcional)" */}
        <div className="campo-grupo">
          <label htmlFor="ingrediente-input" className="etiqueta-campo">Añadir ingredientes (opcional)</label>
          <div className="input-con-boton">
            <input
              type="text"
              id="ingrediente-input"
              className="input-text"
              placeholder="Ingrediente a añadir"
              value={ingrediente}
              onChange={handleIngredienteChange}
            />
            <button
              className="boton-añadir"
              onClick={handleAddIngrediente}
            >
              Añadir
            </button>
          </div>
          {ingredientesList.length > 0 && (
            <div className="ingredientes-agregados">
              <p>Agregados: {ingredientesList.join(', ')}</p>
            </div>
          )}
        </div>

        {/* Botón "Completar Pedido" */}
        <button className="boton-completar-pedido" onClick={handleCompletarPedido}>
          Completar Pedido
        </button>
      </div>

      <p className="info-telefono">También puedes hacer tu pedido por teléfono al (123) 456-7890</p>
    </div>
  );
}

export default RealizarPedido;
