// src/components/Productos.js
import React, { useState } from 'react';
import './Productos.css';
import InfoProductos from './InfoProductos'; // ¡IMPORTACIÓN ACTUALIZADA AQUÍ!

function Productos() {
  // Estado para controlar qué producto se muestra en el modal (null si ninguno)
  const [selectedProduct, setSelectedProduct] = useState(null);

  // Función para abrir el modal con la información del producto
  const handleCardClick = (productName) => {
    setSelectedProduct({ nombre: productName });
  };

  // Función para cerrar el modal
  const handleCloseModal = () => {
    setSelectedProduct(null);
  };

  return (
    <div className="seccion-productos">
      <h1 className="titulo-seccion-productos">Nuestros Productos</h1>
      <hr className="linea-decorativa-amarilla" />

      <div className="galeria-productos">
        {/* Cada tarjeta ahora tiene un onClick */}
        {/* Producto 1: Russa */}
        <div className="tarjeta-producto" onClick={() => handleCardClick('Russa')}>
          <img src="/images/russa.jpg" alt="Russa Kayak Snack" className="imagen-producto" />
          <h3 className="nombre-producto">Russa</h3>
        </div>

        {/* Producto 2: Fruta */}
        <div className="tarjeta-producto" onClick={() => handleCardClick('Fruta')}>
          <img src="/images/frutas.jpeg" alt="Fruta Fresca Kayak Snack" className="imagen-producto" />
          <h3 className="nombre-producto">Fruta</h3>
        </div>

        {/* Producto 3: Elote en vaso */}
        <div className="tarjeta-producto" onClick={() => handleCardClick('Elote en vaso')}>
          <img src="/images/elote.jpeg" alt="Elote en Vaso Kayak Snack" className="imagen-producto elote-imagen" />
          <h3 className="nombre-producto">Elote en vaso</h3>
        </div>

        {/* Producto 4: Tosticeviche */}
        <div className="tarjeta-producto" onClick={() => handleCardClick('Tosticeviche')}>
          <img src="/images/tosticeviche.jpeg" alt="Tosticeviche Kayak Snack" className="imagen-producto tosticeviche-imagen" />
          <h3 className="nombre-producto">Tosticeviche</h3>
        </div>

        {/* Producto 5: Tostielote */}
        <div className="tarjeta-producto" onClick={() => handleCardClick('Tostielote')}>
          <img src="/images/tostielote.jpeg" alt="Tostielote Kayak Snack" className="imagen-producto tostielote-imagen" />
          <h3 className="nombre-producto">Tostielote</h3>
        </div>

        {/* Producto 6: Boyas */}
        <div className="tarjeta-producto" onClick={() => handleCardClick('Boyas')}>
          <img src="/images/boyas.jpeg" alt="Boyas Kayak Snack" className="imagen-producto boyas-imagen" />
          <h3 className="nombre-producto">Boyas</h3>
        </div>

      </div>

      <div className="contenedor-boton-productos">
        <a href="#" className="boton-explorar-productos">Ver más &rarr;</a>
      </div>

      {/* Renderiza el modal si hay un producto seleccionado, usando el nuevo nombre */}
      <InfoProductos producto={selectedProduct} onClose={handleCloseModal} />
    </div>
  );
}

export default Productos;
