// src/App.js
import React from 'react';
import Inicio from './components/Inicio';
import SobreNosotros from './components/SobreNosotros';
import Productos from './components/Productos';
import Ubicacion from './components/Ubicacion';
import RealizarPedido from './components/RealizarPedido';

import './App.css'; // Tus estilos globales

function App() {
  const handleScrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({
        behavior: 'smooth',
        block: 'start'
      });
    }
  };

  return (
    // El .app-container ahora será el que abarque el 100% del ancho y alto
    // y contendrá todo, permitiendo que header y footer se extiendan.
    <div className="app-container">
      {/* HEADER: Ahora tiene un contenedor interno para su contenido centrado */}
      <header>
        <div className="header-content-wrapper"> {/* Nuevo div para el contenido del header */}
          <div className="logo">Kayak Snack</div>
          <nav>
            <ul>
              <li><button onClick={() => handleScrollToSection('inicio-seccion')}>Inicio</button></li>
              <li><button onClick={() => handleScrollToSection('sobre-nosotros-seccion')}>Sobre Nosotros</button></li>
              <li><button onClick={() => handleScrollToSection('productos-seccion')}>Productos</button></li>
              <li><button onClick={() => handleScrollToSection('ubicacion-seccion')}>Ubicación</button></li>
              <li><button onClick={() => handleScrollToSection('realizar-pedido-seccion')}>Realizar Pedido</button></li>
            </ul>
          </nav>
        </div>
      </header>

      {/* MAIN: Contiene todas las secciones. Su contenido se centrará con CSS. */}
      <main>
        {/* Cada componente ahora está envuelto en un div con un ID para el scroll */}
        {/* Las secciones mismas (Inicio, SobreNosotros, etc.) manejarán su propio max-width y centrado */}
        <div id="inicio-seccion">
          <Inicio />
        </div>

        <div id="sobre-nosotros-seccion">
          <SobreNosotros />
        </div>

        <div id="productos-seccion">
          <Productos />
        </div>

        <div id="ubicacion-seccion">
          <Ubicacion />
        </div>

        <div id="realizar-pedido-seccion">
          <RealizarPedido />
        </div>
      </main>

      {/* FOOTER: Ahora tiene un contenedor interno para su contenido centrado */}
      <footer>
        <div className="footer-content-wrapper"> {/* Nuevo div para el contenido del footer */}
          <p>© 2025 Kayak Snack. Todos los derechos reservados.</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
