// src/components/RealizarPedido.js
import React, { useState, useEffect } from 'react';
import '../App.css'; // Asumiendo que App.css contiene los estilos generales, incluyendo .seccion-pagina
import '../components/RealizarPedido.css'; // Estilos espec√≠ficos de este componente

// Supongamos que tienes un array de ingredientes disponibles
const ingredientesDisponibles = [
    'Queso Cotija', 'Mayonesa', 'Aguacate', 'Lim√≥n', 'Chiles Curtidos',
    'Salsa Negra', 'Salsa Valentina', 'Cebolla', 'Cilantro', 'Salsa Verde',
    'Crema', 'Jalape√±o', 'Pepinillos'
];

function RealizarPedido() {
    const [formData, setFormData] = useState({
        nombre: '',
        email: '',
        telefono: '',
        producto: '',
        ingredienteIndividual: '', // Para el input de a√±adir ingrediente
        ingredientes: [], // Lista de ingredientes seleccionados
        notas: '',
        metodoPago: '',
        numeroTarjeta: '',
        fechaExpiracion: '',
        cvv: ''
    });

    const [errors, setErrors] = useState({});
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [showPaymentFields, setShowPaymentFields] = useState(false);
    const [paymentProcessed, setPaymentProcessed] = useState(false);

    // Funci√≥n de validaci√≥n
    const validateForm = () => {
        let newErrors = {};

        // Validaci√≥n de campos obligatorios
        if (!formData.nombre.trim()) {
            newErrors.nombre = 'El nombre es requerido.';
        }
        if (!formData.email.trim()) {
            newErrors.email = 'El email es requerido.';
        } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
            newErrors.email = 'Formato de email inv√°lido.';
        }

        // --- CAMBIO AQU√ç: Se elimina la validaci√≥n de longitud para el tel√©fono ---
        if (!formData.telefono.trim()) {
            newErrors.telefono = 'El tel√©fono es requerido.';
        }
        // ¬°La l√≠nea else if (formData.telefono.length !== 10) ha sido ELIMINADA!
        // ----------------------------------------------------------------------

        if (!formData.producto) {
            newErrors.producto = 'Debes seleccionar un producto.';
        }

        if (showPaymentFields) {
            if (!formData.metodoPago) {
                newErrors.metodoPago = 'Selecciona un m√©todo de pago.';
            }
            if (formData.metodoPago === 'tarjeta') {
                if (!formData.numeroTarjeta.trim() || !/^\d{16}$/.test(formData.numeroTarjeta)) {
                    newErrors.numeroTarjeta = 'N√∫mero de tarjeta inv√°lido (16 d√≠gitos).';
                }
                if (!formData.fechaExpiracion.trim() || !/^(0[1-9]|1[0-2])\/?([0-9]{2})$/.test(formData.fechaExpiracion)) {
                    newErrors.fechaExpiracion = 'Fecha de expiraci√≥n inv√°lida (MM/AA).';
                }
                if (!formData.cvv.trim() || !/^\d{3,4}$/.test(formData.cvv)) {
                    newErrors.cvv = 'CVV inv√°lido (3 o 4 d√≠gitos).';
                }
            }
        }

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    // Manejador gen√©rico de cambios en los inputs
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevData => ({
            ...prevData,
            [name]: value
        }));

        // Limpiar el error cuando el usuario empieza a escribir en el campo
        if (errors[name]) {
            setErrors(prevErrors => ({
                ...prevErrors,
                [name]: ''
            }));
        }
    };

    // Manejador para a√±adir ingredientes
    const handleAddIngredient = () => {
        const ingrediente = formData.ingredienteIndividual.trim();
        if (ingrediente && !formData.ingredientes.includes(ingrediente)) {
            setFormData(prevData => ({
                ...prevData,
                ingredientes: [...prevData.ingredientes, ingrediente],
                ingredienteIndividual: '' // Limpiar el input despu√©s de a√±adir
            }));
        }
    };

    // Manejador para eliminar ingredientes
    const handleDeleteIngredient = (ingredienteToDelete) => {
        setFormData(prevData => ({
            ...prevData,
            ingredientes: prevData.ingredientes.filter(ing => ing !== ingredienteToDelete)
        }));
    };

    // Manejador para el env√≠o del formulario principal
    const handleSubmit = (e) => {
        e.preventDefault();
        setIsSubmitting(true);

        const formIsValid = validateForm();
        if (formIsValid) {
            setShowPaymentFields(true); // Mostrar campos de pago si el formulario principal es v√°lido
            setIsSubmitting(false); // Resetear el estado de env√≠o
        } else {
            console.log('Errores en el formulario principal:', errors);
            setIsSubmitting(false);
        }
    };

    // Manejador para procesar el pago
    const handleProcessPayment = () => {
        const paymentIsValid = validateForm(); // Re-validar incluyendo campos de pago
        if (paymentIsValid) {
            // L√≥gica de procesamiento de pago (simulada)
            console.log('Procesando pago con:', formData.metodoPago, formData.numeroTarjeta);
            setPaymentProcessed(true); // Mostrar mensaje de pago completado
            // Opcional: Reiniciar formulario despu√©s de un tiempo
            // setTimeout(() => {
            //     setFormData({ ...valores iniciales... });
            //     setPaymentProcessed(false);
            //     setShowPaymentFields(false);
            // }, 3000);
        } else {
            console.log('Errores en el pago:', errors);
        }
    };

    // Si paymentProcessed es true, se muestra un mensaje de √©xito
    if (paymentProcessed) {
        return (
            <section className="seccion-realizar-pedido">
                <div className="mensaje-pago-completado">
                    <p>üéâ ¬°Tu pedido ha sido recibido con √©xito!</p>
                    <p>Te enviaremos una confirmaci√≥n a <strong>{formData.email}</strong>.</p>
                    <p>Gracias por tu preferencia.</p>
                </div>
            </section>
        );
    }

    return (
        <section id="realizar-pedido-seccion" className="seccion-realizar-pedido">
            <h2 className="titulo-principal">Realiza tu Pedido</h2>
            <div className="linea-debajo-titulo"></div>
            <p className="subtitulo-principal">
                Completa el siguiente formulario para hacer tu pedido. ¬°Nosotros te lo llevamos!
            </p>

            <form onSubmit={handleSubmit} className="formulario-contenedor">
                <div className="campo-grupo">
                    <label htmlFor="nombre" className="etiqueta-campo">
                        Tu Nombre: <span className="required-asterisk">*</span>
                    </label>
                    <input
                        type="text"
                        id="nombre"
                        name="nombre"
                        className={`input-text ${errors.nombre ? 'input-error' : ''}`}
                        value={formData.nombre}
                        onChange={handleChange}
                        placeholder="Ingresa tu nombre completo"
                        required
                    />
                    {errors.nombre && <p className="mensaje-error">{errors.nombre}</p>}
                </div>

                <div className="campo-grupo">
                    <label htmlFor="email" className="etiqueta-campo">
                        Correo Electr√≥nico: <span className="required-asterisk">*</span>
                    </label>
                    <input
                        type="email"
                        id="email"
                        name="email"
                        className={`input-text ${errors.email ? 'input-error' : ''}`}
                        value={formData.email}
                        onChange={handleChange}
                        placeholder="tu.correo@ejemplo.com"
                        required
                    />
                    {errors.email && <p className="mensaje-error">{errors.email}</p>}
                </div>

                <div className="campo-grupo">
                    <label htmlFor="telefono" className="etiqueta-campo">
                        N√∫mero de Tel√©fono: <span className="required-asterisk">*</span>
                    </label>
                    <input
                        type="tel" // Usar type="tel" es mejor para n√∫meros de tel√©fono
                        id="telefono"
                        name="telefono"
                        className={`input-text ${errors.telefono ? 'input-error' : ''}`}
                        value={formData.telefono}
                        onChange={handleChange}
                        placeholder="Ej. 1234567890"
                        required
                    />
                    {errors.telefono && <p className="mensaje-error">{errors.telefono}</p>}
                </div>

                <div className="campo-grupo">
                    <label htmlFor="producto" className="etiqueta-campo">
                        Selecciona el producto: <span className="required-asterisk">*</span>
                    </label>
                    <select
                        id="producto"
                        name="producto"
                        className={`input-select ${errors.producto ? 'input-error' : ''}`}
                        value={formData.producto}
                        onChange={handleChange}
                        required
                    >
                        <option value="">-- Selecciona un producto --</option>
                        <option value="Tostilocos">Tostilocos</option>
                        <option value="Dorilocos">Dorilocos</option>
                        <option value="Vaso de Elote">Vaso de Elote</option>
                        <option value="Chicharr√≥n Preparado">Chicharr√≥n Preparado</option>
                    </select>
                    {errors.producto && <p className="mensaje-error">{errors.producto}</p>}
                </div>

                {/* Secci√≥n de a√±adir ingredientes */}
                <div className="campo-grupo">
                    <label htmlFor="ingredienteIndividual" className="etiqueta-campo">
                        A√±adir ingredientes (opcional):
                    </label>
                    <div className="ingrediente-input-container">
                        <input
                            type="text"
                            id="ingredienteIndividual"
                            name="ingredienteIndividual"
                            className="input-text"
                            value={formData.ingredienteIndividual}
                            onChange={handleChange}
                            placeholder="Ej. Aguacate"
                            list="ingredientes-sugeridos"
                        />
                        <datalist id="ingredientes-sugeridos">
                            {ingredientesDisponibles.map((ingrediente, index) => (
                                <option key={index} value={ingrediente} />
                            ))}
                        </datalist>
                        <button
                            type="button"
                            onClick={handleAddIngredient}
                            className="boton-anadir-ingrediente"
                            disabled={!formData.ingredienteIndividual.trim()}
                        >
                            A√±adir
                        </button>
                    </div>

                    {formData.ingredientes.length > 0 && (
                        <div className="ingredientes-agregados">
                            <span className="titulo-agregados">Ingredientes agregados:</span>
                            <ul className="lista-agregados">
                                {formData.ingredientes.map((ingrediente, index) => (
                                    <li key={index}>
                                        {ingrediente}
                                        <button
                                            type="button"
                                            onClick={() => handleDeleteIngredient(ingrediente)}
                                            className="boton-eliminar-ingrediente"
                                        >
                                            &times;
                                        </button>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    )}
                </div>

                <div className="campo-grupo">
                    <label htmlFor="notas" className="etiqueta-campo">
                        Notas adicionales (opcional):
                    </label>
                    <textarea
                        id="notas"
                        name="notas"
                        className="input-text"
                        value={formData.notas}
                        onChange={handleChange}
                        placeholder="Ej. sin cebolla, extra picante..."
                        rows="3"
                    ></textarea>
                </div>

                <button type="submit" className="boton-completar-pedido" disabled={isSubmitting}>
                    {isSubmitting ? 'Verificando...' : 'Completar Pedido'}
                </button>
            </form>

            {/* Campos de pago condicionales */}
            {showPaymentFields && (
                <div className="payment-fields-container">
                    <h3>Detalles de Pago</h3>
                    <div className="campo-grupo">
                        <label htmlFor="metodoPago" className="etiqueta-campo">
                            M√©todo de Pago: <span className="required-asterisk">*</span>
                        </label>
                        <select
                            id="metodoPago"
                            name="metodoPago"
                            className={`input-select ${errors.metodoPago ? 'input-error' : ''}`}
                            value={formData.metodoPago}
                            onChange={handleChange}
                            required
                        >
                            <option value="">-- Selecciona un m√©todo --</option>
                            <option value="efectivo">Efectivo al recoger/entregar</option>
                            <option value="tarjeta">Tarjeta de Cr√©dito/D√©bito</option>
                            <option value="paypal">PayPal</option>
                        </select>
                        {errors.metodoPago && <p className="mensaje-error">{errors.metodoPago}</p>}
                    </div>

                    {formData.metodoPago === 'tarjeta' && (
                        <>
                            <div className="campo-grupo">
                                <label htmlFor="numeroTarjeta" className="etiqueta-campo">
                                    N√∫mero de Tarjeta: <span className="required-asterisk">*</span>
                                </label>
                                <input
                                    type="text"
                                    id="numeroTarjeta"
                                    name="numeroTarjeta"
                                    className={`input-text ${errors.numeroTarjeta ? 'input-error' : ''}`}
                                    value={formData.numeroTarjeta}
                                    onChange={handleChange}
                                    placeholder="XXXX XXXX XXXX XXXX"
                                    maxLength="16"
                                    required
                                />
                                {errors.numeroTarjeta && <p className="mensaje-error">{errors.numeroTarjeta}</p>}
                            </div>
                            <div className="campo-row">
                                <div className="campo-col campo-grupo">
                                    <label htmlFor="fechaExpiracion" className="etiqueta-campo">
                                        Fecha de Exp.: <span className="required-asterisk">*</span>
                                    </label>
                                    <input
                                        type="text"
                                        id="fechaExpiracion"
                                        name="fechaExpiracion"
                                        className={`input-text ${errors.fechaExpiracion ? 'input-error' : ''}`}
                                        value={formData.fechaExpiracion}
                                        onChange={handleChange}
                                        placeholder="MM/AA"
                                        maxLength="5"
                                        required
                                    />
                                    {errors.fechaExpiracion && <p className="mensaje-error">{errors.fechaExpiracion}</p>}
                                </div>
                                <div className="campo-col campo-grupo">
                                    <label htmlFor="cvv" className="etiqueta-campo">
                                        CVV: <span className="required-asterisk">*</span>
                                    </label>
                                    <input
                                        type="text"
                                        id="cvv"
                                        name="cvv"
                                        className={`input-text ${errors.cvv ? 'input-error' : ''}`}
                                        value={formData.cvv}
                                        onChange={handleChange}
                                        placeholder="123"
                                        maxLength="4"
                                        required
                                    />
                                    {errors.cvv && <p className="mensaje-error">{errors.cvv}</p>}
                                </div>
                            </div>
                        </>
                    )}

                    <button
                        type="button"
                        onClick={handleProcessPayment}
                        className="boton-procesar-pago"
                        disabled={showPaymentFields && !formData.metodoPago}
                    >
                        Procesar Pago
                    </button>
                </div>
            )}

            <p className="info-telefono">
                Si tienes alguna pregunta, ll√°manos al: <strong>81 2345 6789</strong>
            </p>
        </section>
    );
}

export default RealizarPedido;
