// src/components/RealizarPedido.js
import React, { useState, useEffect } from 'react';
import './RealizarPedido.css';
// Si tienes un componente para el modal de confirmación, impórtalo aquí
// import PedidoConfirmacion from './PedidoConfirmacion';

const RealizarPedido = () => {
    const [formData, setFormData] = useState({
        antojo: '',
        nombre: '',
        telefono: '',
        cantidad: 1, // Valor por defecto
        // notasAdicionales: '', // <-- ELIMINAR ESTO
        metodoEntrega: '',
        ingredientesAdicionales: [],
    });

    const [errores, setErrores] = useState({});
    const [ingredienteInput, setIngredienteInput] = useState('');
    // const [showConfirmationModal, setShowConfirmationModal] = useState(false);
    // const [confirmedOrderData, setConfirmedOrderData] = useState(null);


    const productosSnack = [
        { value: '', label: '-- Elige un producto --', disabled: true },
        { value: 'mango-con-chile', label: 'Mango con Chile' },
        { value: 'bionico', label: 'Biónico' },
        { value: 'vaso-de-fruta', label: 'Vaso de Fruta' },
        { value: 'esquite', label: 'Esquite' },
        { value: 'tostitos', label: 'Tostitos Preparados' },
        { value: 'nachos', label: 'Nachos' },
        { value: 'hot-dog', label: 'Hot Dog' },
        { value: 'agua-fresca', label: 'Agua Fresca' },
        { value: 'cereal', label: 'Cereal con Fruta' },
        { value: 'churro', label: 'Churro con Chocolate' },
        { value: 'palomitas', label: 'Palomitas de Caramelo' },
        { value: 'cafe-frio', label: 'Café Frío' },
    ];

    const metodosEntrega = [
        { value: '', label: '-- Selecciona método --', disabled: true }, // Asumiendo que había un selector
        { value: 'recoger-en-local', label: 'Recoger en local' },
        // Puedes añadir más métodos como 'Envío a domicilio', 'Uber Eats', etc.
    ];

    const validateForm = () => {
        let newErrores = {};
        if (!formData.antojo) {
            newErrores.antojo = 'Selecciona un producto.';
        }
        if (!formData.nombre) {
            newErrores.nombre = 'El nombre es requerido.';
        }
        if (!formData.telefono) {
            newErrores.telefono = 'El teléfono es requerido.';
        } else if (!/^\d{10}$/.test(formData.telefono)) { // Validación básica de 10 dígitos
            newErrores.telefono = 'Formato de teléfono inválido (ej. 8112345678).';
        }
        if (formData.cantidad <= 0) {
            newErrores.cantidad = 'La cantidad debe ser mayor a 0.';
        }
        if (!formData.metodoEntrega) {
            newErrores.metodoEntrega = 'Selecciona un método de entrega.';
        }
        setErrores(newErrores);
        return Object.keys(newErrores).length === 0;
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevData => ({
            ...prevData,
            [name]: value
        }));
    };

    const handleAddIngrediente = () => {
        if (ingredienteInput.trim() !== '' && !formData.ingredientesAdicionales.includes(ingredienteInput.trim())) {
            setFormData(prevData => ({
                ...prevData,
                ingredientesAdicionales: [...prevData.ingredientesAdicionales, ingredienteInput.trim()]
            }));
            setIngredienteInput('');
        }
    };

    const handleRemoveIngrediente = (ingredienteToRemove) => {
        setFormData(prevData => ({
            ...prevData,
            ingredientesAdicionales: prevData.ingredientesAdicionales.filter(ing => ing !== ingredienteToRemove)
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (validateForm()) {
            console.log('Pedido enviado:', formData);
            // Aquí enviarías el formData a tu backend o mostrarías un modal de confirmación
            // setShowConfirmationModal(true);
            // setConfirmedOrderData(formData); // Pasa los datos confirmados al modal

            // Resetear el formulario (opcional)
            setFormData({
                antojo: '',
                nombre: '',
                telefono: '',
                cantidad: 1,
                // notasAdicionales: '', // <-- ELIMINAR ESTO AL RESETEAR
                metodoEntrega: '',
                ingredientesAdicionales: [],
            });
            setIngredienteInput('');
            setErrores({}); // Limpiar errores
            alert('¡Pedido enviado con éxito! Revisa la consola para ver los datos.');
        } else {
            console.log('Errores en el formulario:', errores);
            alert('Por favor, corrige los errores en el formulario.');
        }
    };

    // const handleCloseConfirmation = () => {
    //     setShowConfirmationModal(false);
    //     setConfirmedOrderData(null);
    // };

    // const handlePedirMas = () => {
    //     handleCloseConfirmation();
    //     // Lógica para permitir al usuario añadir más productos o iniciar un nuevo pedido
    // };

    // const handleTerminarPedido = () => {
    //     alert('Pedido Finalizado. ¡Gracias por tu compra!');
    //     handleCloseConfirmation();
    //     // Aquí enviarías el pedido final al backend y quizás redirigirías
    // };


    return (
        <div className="seccion-realizar-pedido">
            <h1 className="titulo-principal">Haz tu Pedido</h1>
            <hr className="linea-debajo-titulo" />
            <p className="subtitulo-principal">Personaliza tu antojo y disfruta de los mejores snacks de Kayak Snack</p>

            <form onSubmit={handleSubmit} className="formulario-contenedor">
                {/* Campo "Selecciona tu antojo" */}
                <div className="campo-grupo">
                    <label htmlFor="antojo" className="etiqueta-campo">
                        Selecciona tu antojo <span className="required-asterisk">*</span>
                    </label>
                    <select
                        id="antojo"
                        name="antojo"
                        className={`input-select ${errores.antojo ? 'input-error' : ''}`}
                        value={formData.antojo}
                        onChange={handleChange}
                    >
                        {productosSnack.map(producto => (
                            <option key={producto.value} value={producto.value} disabled={producto.disabled}>
                                {producto.label}
                            </option>
                        ))}
                    </select>
                    {errores.antojo && <p className="mensaje-error">{errores.antojo}</p>}
                </div>

                {/* Campo "Tu Nombre" */}
                <div className="campo-grupo">
                    <label htmlFor="nombre" className="etiqueta-campo">
                        Tu Nombre: <span className="required-asterisk">*</span>
                    </label>
                    <input
                        type="text"
                        id="nombre"
                        name="nombre"
                        className={`input-text ${errores.nombre ? 'input-error' : ''}`}
                        placeholder="Escribe tu nombre"
                        value={formData.nombre}
                        onChange={handleChange}
                    />
                    {errores.nombre && <p className="mensaje-error">{errores.nombre}</p>}
                </div>

                {/* Campo "Número de Teléfono" */}
                <div className="campo-grupo">
                    <label htmlFor="telefono" className="etiqueta-campo">
                        Número de Teléfono: <span className="required-asterisk">*</span>
                    </label>
                    <input
                        type="tel"
                        id="telefono"
                        name="telefono"
                        className={`input-text ${errores.telefono ? 'input-error' : ''}`}
                        placeholder="Ej: 8112345678"
                        value={formData.telefono}
                        onChange={handleChange}
                    />
                    {errores.telefono && <p className="mensaje-error">{errores.telefono}</p>}
                </div>

                {/* Campo "Cantidad" */}
                <div className="campo-grupo">
                    <label htmlFor="cantidad" className="etiqueta-campo">
                        Cantidad: <span className="required-asterisk">*</span>
                    </label>
                    <input
                        type="number"
                        id="cantidad"
                        name="cantidad"
                        className={`input-text ${errores.cantidad ? 'input-error' : ''}`}
                        value={formData.cantidad}
                        onChange={handleChange}
                        min="1"
                    />
                    {errores.cantidad && <p className="mensaje-error">{errores.cantidad}</p>}
                </div>

                {/* Campo "Ingredientes adicionales (opcional)" - Mantenemos este */}
                <div className="campo-grupo">
                    <label htmlFor="ingrediente-input" className="etiqueta-campo">
                        Ingredientes adicionales (opcional):
                    </label>
                    <div className="input-con-boton">
                        <input
                            type="text"
                            id="ingrediente-input"
                            className="input-text"
                            placeholder="Ej: extra chamoy, sin chile"
                            value={ingredienteInput}
                            onChange={(e) => setIngredienteInput(e.target.value)}
                            onKeyPress={(e) => {
                                if (e.key === 'Enter') {
                                    e.preventDefault(); // Evita el submit del formulario
                                    handleAddIngrediente();
                                }
                            }}
                        />
                        <button type="button" onClick={handleAddIngrediente} className="boton-añadir">
                            Añadir
                        </button>
                    </div>
                    {formData.ingredientesAdicionales.length > 0 && (
                        <div className="ingredientes-agregados">
                            <p className="titulo-agregados">Agregados:</p>
                            <ul className="lista-agregados">
                                {formData.ingredientesAdicionales.map((ing, index) => (
                                    <li key={index}>
                                        {ing}
                                        <button type="button" onClick={() => handleRemoveIngrediente(ing)} className="boton-eliminar-ingrediente">
                                            &times;
                                        </button>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    )}
                </div>

                {/* ELIMINAMOS EL CAMPO "NOTAS ADICIONALES" DE AQUÍ */}
                {/*
                <div className="campo-grupo">
                    <label htmlFor="notas" className="etiqueta-campo">
                        Notas adicionales (opcional):
                    </label>
                    <textarea
                        id="notas"
                        name="notasAdicionales"
                        className="input-text" // Usamos la misma clase para estilizarlo como input
                        placeholder="Ej: sin picante, con mucha crema, etc."
                        value={formData.notasAdicionales}
                        onChange={handleChange}
                        rows="4" // Define la altura inicial del textarea
                    ></textarea>
                </div>
                */}

                {/* Campo "Método de entrega" */}
                <div className="campo-grupo">
                    <label htmlFor="metodoEntrega" className="etiqueta-campo">
                        Método de entrega: <span className="required-asterisk">*</span>
                    </label>
                    <select
                        id="metodoEntrega"
                        name="metodoEntrega"
                        className={`input-select ${errores.metodoEntrega ? 'input-error' : ''}`}
                        value={formData.metodoEntrega}
                        onChange={handleChange}
                    >
                        {metodosEntrega.map(metodo => (
                            <option key={metodo.value} value={metodo.value} disabled={metodo.disabled}>
                                {metodo.label}
                            </option>
                        ))}
                    </select>
                    {errores.metodoEntrega && <p className="mensaje-error">{errores.metodoEntrega}</p>}
                </div>

                <button type="submit" className="boton-completar-pedido">
                    Completar Pedido
                </button>
            </form>

            <p className="info-telefono">
                Si prefieres, también puedes hacer tu pedido llamando al: <strong>81 1234 5678</strong>
            </p>

            {/* Renderiza el modal de confirmación si showConfirmationModal es true */}
            {/* {showConfirmationModal && confirmedOrderData && (
                <PedidoConfirmacion
                    pedido={confirmedOrderData}
                    onClose={handleCloseConfirmation}
                    onPedirMas={handlePedirMas}
                    onTerminarPedido={handleTerminarPedido}
                    onCancelar={handleCloseConfirmation} // Añadir esta prop para cancelar desde el modal
                />
            )} */}
        </div>
    );
};

export default RealizarPedido;
