// src/components/RealizarPedido.js
import React, { useState } from 'react';
import './RealizarPedido.css';

// Definición de productos y sus ingredientes.
// Puedes expandir esta lista con todos tus productos e ingredientes.
const productosConIngredientes = {
  "Fruta Fresca": ["Chile en polvo", "Chamoy", "Tajín", "Limón", "Sal"],
  "Elote en vaso": ["Mayonesa", "Queso", "Chile en polvo", "Limón", "Crema"],
  "Tosticeviche": ["Aguacate", "Salsa picante", "Crema", "Cebolla", "Cilantro"],
  "Tostielote": ["Aguacate", "Salsa picante", "Crema", "Cebolla", "Cilantro", "Queso"],
  "Litro Ceviche": ["Cebolla", "Cilantro", "Aguacate", "Pepino", "Salsa picante"],
  "Chilidog": ["Cebolla picada", "Jalapeños", "Mostaza", "Cátsup", "Pepinillos"],
  // Añade más productos aquí, siguiendo el formato:
  // "Nombre del Producto": ["Ingrediente 1", "Ingrediente 2", ...],
};

function RealizarPedido() {
  const [selectedAntojo, setSelectedAntojo] = useState('');
  const [ingredienteSeleccionado, setIngredienteSeleccionado] = useState(''); // Para el select de ingredientes
  const [ingredientesAgregados, setIngredientesAgregados] = useState([]);
  const [ingredientesDisponibles, setIngredientesDisponibles] = useState([]);

  const handleAntojoChange = (event) => {
    const antojo = event.target.value;
    setSelectedAntojo(antojo);
    // Actualiza los ingredientes disponibles cuando cambia el antojo
    setIngredientesDisponibles(productosConIngredientes[antojo] || []);
    // Reinicia los ingredientes seleccionados y agregados al cambiar el antojo
    setIngredienteSeleccionado('');
    setIngredientesAgregados([]);
  };

  const handleIngredienteSelectChange = (event) => {
    setIngredienteSeleccionado(event.target.value);
  };

  const handleAddIngrediente = () => {
    if (ingredienteSeleccionado && !ingredientesAgregados.includes(ingredienteSeleccionado)) {
      setIngredientesAgregados([...ingredientesAgregados, ingredienteSeleccionado]);
      setIngredienteSeleccionado(''); // Limpia la selección después de añadir
    }
  };

  const handleRemoveIngrediente = (ingredienteToRemove) => {
    setIngredientesAgregados(ingredientesAgregados.filter(ing => ing !== ingredienteToRemove));
  };

  const handleCompletarPedido = () => {
    // Aquí puedes añadir la lógica para enviar el pedido
    const pedido = {
      antojo: selectedAntojo,
      ingredientes: ingredientesAgregados,
    };
    alert(`Pedido completado:\nAntojo: ${pedido.antojo}\nIngredientes: ${pedido.ingredientes.join(', ')}`);
    // Opcional: resetear el formulario después de completar el pedido
    setSelectedAntojo('');
    setIngredienteSeleccionado('');
    setIngredientesAgregados([]);
    setIngredientesDisponibles([]);
  };

  return (
    <div className="seccion-realizar-pedido">
      <h1 className="titulo-pedido">Haz tu Pedido</h1>
      <p className="subtitulo-pedido">Personaliza tu antojo y disfruta de los mejores snacks de Kayak Snack</p>

      <div className="contenedor-formulario-pedido">
        {/* Sección "Selecciona tu antojo" */}
        <div className="campo-grupo">
          <label htmlFor="antojo-select" className="etiqueta-campo">Selecciona tu antojo</label>
          <select
            id="antojo-select"
            className="input-select"
            value={selectedAntojo}
            onChange={handleAntojoChange}
          >
            <option value="">Selecciona una opción</option>
            {Object.keys(productosConIngredientes).map((producto) => (
              <option key={producto} value={producto}>{producto}</option>
            ))}
          </select>
        </div>

        {/* Sección "Añadir ingredientes (opcional)" */}
        {selectedAntojo && ( // Solo muestra esta sección si se ha seleccionado un antojo
          <div className="campo-grupo">
            <label htmlFor="ingrediente-select" className="etiqueta-campo">Añadir ingredientes (opcional)</label>
            <div className="input-con-boton">
              <select
                id="ingrediente-select"
                className="input-select" // Reutilizamos el estilo del select
                value={ingredienteSeleccionado}
                onChange={handleIngredienteSelectChange}
                disabled={ingredientesDisponibles.length === 0} // Deshabilita si no hay ingredientes
              >
                <option value="">Selecciona un ingrediente</option>
                {ingredientesDisponibles.map((ingrediente) => (
                  <option key={ingrediente} value={ingrediente}>{ingrediente}</option>
                ))}
              </select>
              <button
                className="boton-añadir"
                onClick={handleAddIngrediente}
                disabled={!ingredienteSeleccionado} // Deshabilita si no hay ingrediente seleccionado
              >
                Añadir
              </button>
            </div>
            {ingredientesAgregados.length > 0 && (
              <div className="ingredientes-agregados">
                <p className="titulo-agregados">Ingredientes agregados:</p>
                <ul className="lista-agregados">
                  {ingredientesAgregados.map((ingrediente, index) => (
                    <li key={index}>
                      {ingrediente}
                      <button className="boton-eliminar-ingrediente" onClick={() => handleRemoveIngrediente(ingrediente)}>x</button>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        )}

        {/* Botón "Completar Pedido" */}
        <button
          className="boton-completar-pedido"
          onClick={handleCompletarPedido}
          disabled={!selectedAntojo} // Deshabilita si no hay antojo seleccionado
        >
          Completar Pedido
        </button>
      </div>

      <p className="info-telefono">También puedes hacer tu pedido por teléfono al (123) 456-7890</p>
    </div>
  );
}

export default RealizarPedido;
