// src/components/IngredienteInputs.js
import React from 'react';

const IngredienteInputs = ({
    label,
    placeholder,
    suggestions, // Array de strings con las sugerencias de ingredientes
    currentValue,
    onValueChange,
    onAddClick,
    isDisabled,
    errorMessage
}) => {
    // Genera un ID único para el datalist para que coincida con el input
    // Esto es crucial si tuvieras múltiples instancias de IngredienteInputs en la misma página
    const dataListId = `suggestions-${Math.random().toString(36).substring(2, 9)}`;

    return (
        <div className="campo-grupo"> {/* Reutiliza la clase de RealizarPedido.css */}
            <label htmlFor="ingrediente-input" className="etiqueta-campo"> {/* Reutiliza la clase de RealizarPedido.css */}
                {label}
            </label>
            <div className="input-con-boton"> {/* Reutiliza la clase de RealizarPedido.css */}
                <input
                    type="text"
                    id="ingrediente-input" // Puedes hacer este ID dinámico también si lo necesitas
                    className="input-text" // Reutiliza la clase de RealizarPedido.css
                    placeholder={placeholder}
                    value={currentValue}
                    onChange={(e) => onValueChange(e.target.value)}
                    onKeyPress={(e) => {
                        if (e.key === 'Enter') {
                            e.preventDefault(); // Evita que se envíe el formulario si hay un botón de submit
                            onAddClick();
                        }
                    }}
                    list={dataListId} // ¡IMPORTANTE! Conecta el input con el datalist
                />
                <button
                    type="button"
                    onClick={onAddClick}
                    className="boton-añadir" // Reutiliza la clase de RealizarPedido.css
                    disabled={isDisabled}
                >
                    Añadir
                </button>
                {/* ¡EL DATALIST QUE MUESTRA LAS OPCIONES! */}
                {/* Solo se renderiza si hay sugerencias para evitar un DOM vacío innecesario */}
                {suggestions && suggestions.length > 0 && (
                    <datalist id={dataListId}>
                        {suggestions.map((ingrediente, index) => (
                            <option key={index} value={ingrediente} />
                        ))}
                    </datalist>
                )}
            </div>
            {errorMessage && <p className="mensaje-error">{errorMessage}</p>} {/* Reutiliza la clase de RealizarPedido.css */}
        </div>
    );
};

export default IngredienteInputs;
