// src/components/RealizarPedido.js
import React, { useState, useEffect } from 'react';
import './RealizarPedido.css'; // Asegúrate de que este CSS existe y está vinculado

function RealizarPedido() {
    // Definición de productos y sus ingredientes específicos
    const productosConfig = {
        'Fruta Fresca': ['Chamoy', 'Tajín', 'Miguelito', 'Leche Condensada', 'Sal', 'Limón'],
        'Elote en vaso': ['Queso Cotija', 'Mayonesa', 'Chili en polvo', 'Crema', 'Mantequilla'],
        // Puedes añadir más productos con sus propios ingredientes aquí
        'Russa': [], // Por ejemplo, la Russa no tiene ingredientes extra seleccionables
        'Tosticeviche': [],
        'Tostielote': [],
        'Boyas': [],
        'Tostada': [],
        'Litro Ceviche': [],
        'Chilidog': [],
        // ... otros productos que no tienen ingredientes extra
    };

    // Lista de todos los productos disponibles (para el dropdown)
    const productosDisponibles = Object.keys(productosConfig);

    // Estado para los datos del formulario
    const [formData, setFormData] = useState({
        nombre: '',
        telefono: '',
        producto: '',
        ingredientes: [], // Guarda los ingredientes seleccionados para el producto actual
        cantidad: 1,
        notas: '',
        metodoEntrega: 'recoger',
        direccion: '',
    });

    // Estado para controlar errores de validación
    const [errors, setErrors] = useState({});

    // Estado para el ingrediente actualmente seleccionado en el input de añadir
    const [currentIngredienteInput, setCurrentIngredienteInput] = useState('');

    // Obtener los ingredientes disponibles para el producto seleccionado
    const ingredientesParaProductoActual = productosConfig[formData.producto] || [];

    // Manejador genérico para cambios en los inputs del formulario
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));

        // Limpia errores relacionados con el campo que se está modificando
        setErrors(prev => ({ ...prev, [name]: '' }));

        // Lógica específica cuando el producto cambia:
        if (name === 'producto') {
            setFormData(prev => ({
                ...prev,
                ingredientes: [] // Limpiar ingredientes si el producto cambia
            }));
            setCurrentIngredienteInput(''); // Limpiar el input de ingredientes
        }
    };

    // Manejador para añadir un ingrediente a la lista
    const handleAddIngrediente = () => {
        const ingredienteToAdd = currentIngredienteInput.trim();

        if (ingredienteToAdd && ingredientesParaProductoActual.includes(ingredienteToAdd) && !formData.ingredientes.includes(ingredienteToAdd)) {
            setFormData(prev => ({
                ...prev,
                ingredientes: [...prev.ingredientes, ingredienteToAdd]
            }));
            setCurrentIngredienteInput(''); // Limpia el input después de añadir
            setErrors(prev => ({ ...prev, ingredientes: '' })); // Limpia error si existía
        } else if (ingredienteToAdd && formData.ingredientes.includes(ingredienteToAdd)) {
            // Feedback si el ingrediente ya fue añadido
            alert(`"${ingredienteToAdd}" ya ha sido añadido.`);
        } else if (ingredienteToAdd && !ingredientesParaProductoActual.includes(ingredienteToAdd)) {
             alert(`"${ingredienteToAdd}" no es un ingrediente válido para "${formData.producto}".`);
        }
    };

    // Manejador para eliminar un ingrediente
    const handleRemoveIngrediente = (ingredienteToRemove) => {
        setFormData(prev => ({
            ...prev,
            ingredientes: prev.ingredientes.filter(ing => ing !== ingredienteToRemove)
        }));
    };

    // Función de validación del formulario
    const validateForm = () => {
        let newErrors = {};
        let isValid = true;

        if (!formData.nombre.trim()) {
            newErrors.nombre = 'El nombre es requerido.';
            isValid = false;
        }

        if (!formData.telefono.trim()) {
            newErrors.telefono = 'El teléfono es requerido.';
            isValid = false;
        } else if (!/^\d{10}$/.test(formData.telefono.trim())) { // Asume un teléfono de 10 dígitos
            newErrors.telefono = 'El teléfono debe tener 10 dígitos.';
            isValid = false;
        }

        if (!formData.producto) {
            newErrors.producto = 'Selecciona un producto.';
            isValid = false;
        }

        if (formData.cantidad < 1) {
            newErrors.cantidad = 'La cantidad debe ser al menos 1.';
            isValid = false;
        }

        if (formData.metodoEntrega === 'domicilio' && !formData.direccion.trim()) {
            newErrors.direccion = 'La dirección es requerida para el servicio a domicilio.';
            isValid = false;
        }

        setErrors(newErrors);
        return isValid;
    };

    // Manejador para enviar el formulario
    const handleSubmit = (e) => {
        e.preventDefault();

        if (validateForm()) {
            console.log("Pedido Enviado:", formData);
            alert('¡Tu pedido ha sido enviado con éxito! Nos pondremos en contacto contigo pronto.');

            // Opcional: Reiniciar el formulario
            setFormData({
                nombre: '',
                telefono: '',
                producto: '',
                ingredientes: [],
                cantidad: 1,
                notas: '',
                metodoEntrega: 'recoger',
                direccion: '',
            });
            setErrors({});
            setCurrentIngredienteInput('');
        } else {
            alert('Por favor, completa todos los campos requeridos y corrige los errores.');
            const firstErrorField = Object.keys(errors)[0];
            if (firstErrorField) {
                document.getElementById(firstErrorField)?.focus();
            }
        }
    };

    // Lógica para deshabilitar el botón de añadir ingrediente
    const isAddIngredienteDisabled = !currentIngredienteInput.trim() || !ingredientesParaProductoActual.includes(currentIngredienteInput.trim()) || formData.ingredientes.includes(currentIngredienteInput.trim());

    // Lógica para deshabilitar el botón de enviar pedido
    const [isSubmitDisabled, setIsSubmitDisabled] = useState(true);

    useEffect(() => {
        // Ejecuta la validación y actualiza el estado de disabled del botón
        const isValid = validateForm();
        setIsSubmitDisabled(!isValid);
    }, [formData]); // Dependencia: re-evaluar cuando formData cambie

    return (
        <div className="seccion-realizar-pedido">
            <h1 className="titulo-principal">Haz tu Pedido</h1>
            <hr className="linea-debajo-titulo" />
            <p className="subtitulo-principal">Personaliza tu antojo y disfruta de los mejores snacks de Kayak Snack</p>

            <form onSubmit={handleSubmit} className="formulario-contenedor">
                {/* Campo Producto (ya no es un label, ahora es un título dentro del card) */}
                <div className="campo-grupo">
                    <label htmlFor="producto" className="etiqueta-campo">Selecciona tu antojo <span className="required-asterisk">*</span></label>
                    <select
                        id="producto"
                        name="producto"
                        value={formData.producto}
                        onChange={handleChange}
                        className={`input-select ${errors.producto ? 'input-error' : ''}`}
                        aria-invalid={errors.producto ? "true" : "false"}
                        aria-describedby="error-producto"
                    >
                        <option value="">-- Elige un producto --</option>
                        {productosDisponibles.map(producto => (
                            <option key={producto} value={producto}>{producto}</option>
                        ))}
                    </select>
                    {errors.producto && <p id="error-producto" className="mensaje-error">{errors.producto}</p>}
                </div>

                {/* Sección de añadir ingredientes (mostrar solo si el producto tiene ingredientes definidos) */}
                {ingredientesParaProductoActual.length > 0 && (
                    <div className="campo-grupo">
                        <label htmlFor="ingredienteInput" className="etiqueta-campo">Añadir ingredientes (opcional)</label>
                        <div className="input-con-boton">
                            <input
                                type="text"
                                id="ingredienteInput"
                                name="ingredienteInput"
                                value={currentIngredienteInput}
                                onChange={(e) => setCurrentIngredienteInput(e.target.value)}
                                className="input-text"
                                placeholder="Ingrediente a añadir"
                                list="ingredientes-sugeridos" // Para la lista de sugerencias
                            />
                            <datalist id="ingredientes-sugeridos">
                                {ingredientesParaProductoActual.map(ingrediente => (
                                    <option key={ingrediente} value={ingrediente} />
                                ))}
                            </datalist>
                            <button
                                type="button"
                                onClick={handleAddIngrediente}
                                className="boton-añadir"
                                disabled={isAddIngredienteDisabled}
                            >
                                Añadir
                            </button>
                        </div>

                        {/* Mostrar ingredientes ya añadidos */}
                        {formData.ingredientes.length > 0 && (
                            <div className="ingredientes-agregados">
                                <p className="titulo-agregados">Agregados:</p>
                                <ul className="lista-agregados">
                                    {formData.ingredientes.map(ingrediente => (
                                        <li key={ingrediente}>
                                            {ingrediente}
                                            <button
                                                type="button"
                                                onClick={() => handleRemoveIngrediente(ingrediente)}
                                                className="boton-eliminar-ingrediente"
                                                title="Eliminar ingrediente"
                                            >
                                                &times;
                                            </button>
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        )}
                    </div>
                )}

                {/* Campos restantes del formulario */}
                {/* Campo Nombre */}
                <div className="campo-grupo">
                    <label htmlFor="nombre" className="etiqueta-campo">Tu Nombre: <span className="required-asterisk">*</span></label>
                    <input
                        type="text"
                        id="nombre"
                        name="nombre"
                        value={formData.nombre}
                        onChange={handleChange}
                        className={`input-text ${errors.nombre ? 'input-error' : ''}`}
                        placeholder="Escribe tu nombre"
                        aria-invalid={errors.nombre ? "true" : "false"}
                        aria-describedby="error-nombre"
                    />
                    {errors.nombre && <p id="error-nombre" className="mensaje-error">{errors.nombre}</p>}
                </div>

                {/* Campo Teléfono */}
                <div className="campo-grupo">
                    <label htmlFor="telefono" className="etiqueta-campo">Número de Teléfono: <span className="required-asterisk">*</span></label>
                    <input
                        type="tel"
                        id="telefono"
                        name="telefono"
                        value={formData.telefono}
                        onChange={handleChange}
                        className={`input-text ${errors.telefono ? 'input-error' : ''}`}
                        placeholder="Ej: 8112345678"
                        aria-invalid={errors.telefono ? "true" : "false"}
                        aria-describedby="error-telefono"
                    />
                    {errors.telefono && <p id="error-telefono" className="mensaje-error">{errors.telefono}</p>}
                </div>

                {/* Campo Cantidad */}
                <div className="campo-grupo">
                    <label htmlFor="cantidad" className="etiqueta-campo">Cantidad: <span className="required-asterisk">*</span></label>
                    <input
                        type="number"
                        id="cantidad"
                        name="cantidad"
                        value={formData.cantidad}
                        onChange={handleChange}
                        className={`input-text ${errors.cantidad ? 'input-error' : ''}`}
                        min="1"
                        aria-invalid={errors.cantidad ? "true" : "false"}
                        aria-describedby="error-cantidad"
                    />
                    {errors.cantidad && <p id="error-cantidad" className="mensaje-error">{errors.cantidad}</p>}
                </div>

                {/* Campo Notas Adicionales */}
                <div className="campo-grupo">
                    <label htmlFor="notas" className="etiqueta-campo">Notas adicionales (opcional):</label>
                    <textarea
                        id="notas"
                        name="notas"
                        value={formData.notas}
                        onChange={handleChange}
                        className="input-text"
                        rows="4"
                        placeholder="Ej: sin picante, con mucha crema, etc."
                    ></textarea>
                </div>

                {/* Campo Método de Entrega */}
                <div className="campo-grupo">
                    <label htmlFor="metodoEntrega" className="etiqueta-campo">Método de entrega: <span className="required-asterisk">*</span></label>
                    <select
                        id="metodoEntrega"
                        name="metodoEntrega"
                        value={formData.metodoEntrega}
                        onChange={handleChange}
                        className="input-select"
                    >
                        <option value="recoger">Recoger en local</option>
                        <option value="domicilio">Servicio a domicilio</option>
                    </select>
                </div>

                {/* Campo Dirección (solo si el método de entrega es "domicilio") */}
                {formData.metodoEntrega === 'domicilio' && (
                    <div className="campo-grupo">
                        <label htmlFor="direccion" className="etiqueta-campo">Dirección de entrega: <span className="required-asterisk">*</span></label>
                        <input
                            type="text"
                            id="direccion"
                            name="direccion"
                            value={formData.direccion}
                            onChange={handleChange}
                            className={`input-text ${errors.direccion ? 'input-error' : ''}`}
                            placeholder="Calle, número, colonia, código postal"
                            aria-invalid={errors.direccion ? "true" : "false"}
                            aria-describedby="error-direccion"
                        />
                        {errors.direccion && <p id="error-direccion" className="mensaje-error">{errors.direccion}</p>}
                    </div>
                )}

                {/* Botón para completar el pedido */}
                <button
                    type="submit"
                    className="boton-completar-pedido"
                    disabled={isSubmitDisabled}
                >
                    Completar Pedido
                </button>
            </form>

            <p className="info-telefono">
                Si prefieres, también puedes hacer tu pedido llamando al: <strong>81 1234 5678</strong>
            </p>
        </div>
    );
}

export default RealizarPedido;
