// src/components/RealizarPedido.js
import React, { useState, useEffect } from 'react'; // Importamos useEffect
import './RealizarPedido.css';
import PedidoConfirmacion from './PedidoConfirmacion';

const productosConIngredientes = {
  "Fruta Fresca": {
    ingredientes: ["Chile en polvo", "Chamoy", "Tajín", "Limón", "Sal"],
    imagen: "https://via.placeholder.com/150/FFD700/FFFFFF?text=Fruta+Fresca",
    tiempoBaseMin: 5, // Tiempo base en minutos para este producto
    tiempoBaseMax: 10,
  },
  "Elote en vaso": {
    ingredientes: ["Mayonesa", "Queso", "Chile en polvo", "Limón", "Crema"],
    imagen: "https://via.placeholder.com/150/FFD700/FFFFFF?text=Elote+en+vaso",
    tiempoBaseMin: 7,
    tiempoBaseMax: 12,
  },
  "Tosticeviche": {
    ingredientes: ["Aguacate", "Salsa picante", "Crema", "Cebolla", "Cilantro"],
    imagen: "https://via.placeholder.com/150/FFD700/FFFFFF?text=Tosticeviche",
    tiempoBaseMin: 15,
    tiempoBaseMax: 25,
  },
  "Tostielote": {
    ingredientes: ["Aguacate", "Salsa picante", "Crema", "Cebolla", "Cilantro", "Queso"],
    imagen: "https://via.placeholder.com/150/FFD700/FFFFFF?text=Tostielote",
    tiempoBaseMin: 10,
    tiempoBaseMax: 20,
  },
  "Litro Ceviche": {
    ingredientes: ["Cebolla", "Cilantro", "Aguacate", "Pepino", "Salsa picante"],
    imagen: "https://via.placeholder.com/150/FFD700/FFFFFF?text=Litro+Ceviche",
    tiempoBaseMin: 20,
    tiempoBaseMax: 35,
  },
  "Chilidog": {
    ingredientes: ["Cebolla picada", "Jalapeños", "Mostaza", "Cátsup", "Pepinillos"],
    imagen: "https://via.placeholder.com/150/FFD700/FFFFFF?text=Chilidog",
    tiempoBaseMin: 8,
    tiempoBaseMax: 15,
  },
};

const TIEMPO_POR_INGREDIENTE = 1; // Minutos extra por cada ingrediente adicional
const TIEMPO_ENVIO_MIN = 15; // Tiempo mínimo de envío
const TIEMPO_ENVIO_MAX = 30; // Tiempo máximo de envío

function RealizarPedido() {
  const [selectedAntojo, setSelectedAntojo] = useState('');
  const [ingredienteSeleccionado, setIngredienteSeleccionado] = useState('');
  const [ingredientesAgregados, setIngredientesAgregados] = useState([]);
  const [ingredientesDisponibles, setIngredientesDisponibles] = useState([]);

  const [tipoServicio, setTipoServicio] = useState('');
  const [direccionEnvio, setDireccionEnvio] = useState('');

  const [mostrarModal, setMostrarModal] = useState(false);
  const [pedidoActual, setPedidoActual] = useState(null);
  const [tiempoEstimado, setTiempoEstimado] = useState(''); // NUEVO ESTADO para el tiempo estimado

  // Función para calcular el tiempo estimado
  const calcularTiempoEstimado = () => {
    let minTiempo = 0;
    let maxTiempo = 0;

    if (selectedAntojo && productosConIngredientes[selectedAntojo]) {
      minTiempo += productosConIngredientes[selectedAntojo].tiempoBaseMin;
      maxTiempo += productosConIngredientes[selectedAntojo].tiempoBaseMax;

      // Sumar tiempo por ingredientes adicionales
      minTiempo += ingredientesAgregados.length * TIEMPO_POR_INGREDIENTE;
      maxTiempo += ingredientesAgregados.length * TIEMPO_POR_INGREDIENTE;

      // Sumar tiempo de envío si es a domicilio
      if (tipoServicio === 'domicilio') {
        minTiempo += TIEMPO_ENVIO_MIN;
        maxTiempo += TIEMPO_ENVIO_MAX;
      }

      setTiempoEstimado(`${minTiempo}-${maxTiempo} minutos`);
    } else {
      setTiempoEstimado('');
    }
  };

  // Usar useEffect para recalcular el tiempo estimado cada vez que cambien
  // el antojo seleccionado, los ingredientes o el tipo de servicio.
  useEffect(() => {
    calcularTiempoEstimado();
  }, [selectedAntojo, ingredientesAgregados, tipoServicio]);


  const handleAntojoChange = (event) => {
    const antojo = event.target.value;
    setSelectedAntojo(antojo);
    setIngredientesDisponibles(productosConIngredientes[antojo]?.ingredientes || []);
    setIngredienteSeleccionado('');
    setIngredientesAgregados([]);
  };

  const handleIngredienteSelectChange = (event) => {
    setIngredienteSeleccionado(event.target.value);
  };

  const handleAddIngrediente = () => {
    if (ingredienteSeleccionado && !ingredientesAgregados.includes(ingredienteSeleccionado)) {
      setIngredientesAgregados([...ingredientesAgregados, ingredienteSeleccionado]);
      setIngredienteSeleccionado('');
    }
  };

  const handleRemoveIngrediente = (ingredienteToRemove) => {
    setIngredientesAgregados(ingredientesAgregados.filter(ing => ing !== ingredienteToRemove));
  };

  const handleTipoServicioChange = (event) => {
    setTipoServicio(event.target.value);
    if (event.target.value === 'recoger') {
      setDireccionEnvio('');
    }
  };

  const handleDireccionEnvioChange = (event) => {
    setDireccionEnvio(event.target.value);
  };

  const handleCompletarPedido = () => {
    if (tipoServicio === 'domicilio' && !direccionEnvio.trim()) {
      alert('Por favor, ingresa tu dirección de envío.');
      return;
    }
    if (!tipoServicio) {
        alert('Por favor, selecciona un tipo de servicio (Recoger o Domicilio).');
        return;
    }
    if (!selectedAntojo) { // Añadimos validación para el antojo
        alert('Por favor, selecciona un antojo.');
        return;
    }


    const pedido = {
      antojo: selectedAntojo,
      ingredientes: ingredientesAgregados,
      imagen: productosConIngredientes[selectedAntojo]?.imagen,
      tipoServicio: tipoServicio,
      direccionEnvio: tipoServicio === 'domicilio' ? direccionEnvio : 'N/A',
      tiempoEstimado: tiempoEstimado, // Agregamos el tiempo estimado al pedido
    };
    setPedidoActual(pedido);
    setMostrarModal(true);
  };

  const handlePedirMas = () => {
    setMostrarModal(false);
    setSelectedAntojo('');
    setIngredienteSeleccionado('');
    setIngredientesAgregados([]);
    setIngredientesDisponibles([]);
    setPedidoActual(null);
    setTipoServicio('');
    setDireccionEnvio('');
    setTiempoEstimado(''); // Reinicia el tiempo estimado
  };

  const handleCancelarPedido = () => {
    setMostrarModal(false);
    setSelectedAntojo('');
    setIngredienteSeleccionado('');
    setIngredientesAgregados([]);
    setIngredientesDisponibles([]);
    setPedidoActual(null);
    setTipoServicio('');
    setDireccionEnvio('');
    setTiempoEstimado(''); // Reinicia el tiempo estimado
    alert('Pedido cancelado.');
  };

  const handleTerminarPedido = () => {
    setMostrarModal(false);
    alert(`¡Pedido finalizado con éxito!\nAntojo: ${pedidoActual.antojo}\nIngredientes: ${pedidoActual.ingredientes.join(', ')}\nTipo de Servicio: ${pedidoActual.tipoServicio === 'recoger' ? 'Recoger en tienda' : `Envío a Domicilio (${pedidoActual.direccionEnvio})`}\nTiempo Estimado: ${pedidoActual.tiempoEstimado}\nGracias por tu compra.`);
    setSelectedAntojo('');
    setIngredienteSeleccionado('');
    setIngredientesAgregados([]);
    setIngredientesDisponibles([]);
    setPedidoActual(null);
    setTipoServicio('');
    setDireccionEnvio('');
    setTiempoEstimado(''); // Reinicia el tiempo estimado
  };

  // Deshabilitar el botón si no hay antojo, servicio, o dirección si es a domicilio
  const isCompletarPedidoDisabled = !selectedAntojo || !tipoServicio || (tipoServicio === 'domicilio' && !direccionEnvio.trim());

  return (
    <div className="seccion-realizar-pedido">
      <h1 className="titulo-pedido">Haz tu Pedido</h1>
      <p className="subtitulo-pedido">Personaliza tu antojo y disfruta de los mejores snacks de Kayak Snack</p>

      <div className="contenedor-formulario-pedido">
        {/* Sección "Selecciona tu antojo" */}
        <div className="campo-grupo">
          <label htmlFor="antojo-select" className="etiqueta-campo">Selecciona tu antojo</label>
          <select
            id="antojo-select"
            className="input-select"
            value={selectedAntojo}
            onChange={handleAntojoChange}
          >
            <option value="">Selecciona una opción</option>
            {Object.keys(productosConIngredientes).map((producto) => (
              <option key={producto} value={producto}>{producto}</option>
            ))}
          </select>
        </div>

        {/* Sección "Añadir ingredientes (opcional)" */}
        {selectedAntojo && (
          <div className="campo-grupo">
            <label htmlFor="ingrediente-select" className="etiqueta-campo">Añadir ingredientes (opcional)</label>
            <div className="input-con-boton">
              <select
                id="ingrediente-select"
                className="input-select"
                value={ingredienteSeleccionado}
                onChange={handleIngredienteSelectChange}
                disabled={ingredientesDisponibles.length === 0}
              >
                <option value="">Selecciona un ingrediente</option>
                {ingredientesDisponibles
                    .filter(ing => !ingredientesAgregados.includes(ing))
                    .map((ingrediente) => (
                    <option key={ingrediente} value={ingrediente}>{ingrediente}</option>
                ))}
              </select>
              <button
                className="boton-añadir"
                onClick={handleAddIngrediente}
                disabled={!ingredienteSeleccionado}
              >
                Añadir
              </button>
            </div>
            {ingredientesAgregados.length > 0 && (
              <div className="ingredientes-agregados">
                <p className="titulo-agregados">Ingredientes agregados:</p>
                <ul className="lista-agregados">
                  {ingredientesAgregados.map((ingrediente, index) => (
                    <li key={index}>
                      {ingrediente}
                      <button className="boton-eliminar-ingrediente" onClick={() => handleRemoveIngrediente(ingrediente)}>x</button>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        )}

        {/* Sección Tipo de Servicio */}
        <div className="campo-grupo">
          <label htmlFor="tipo-servicio" className="etiqueta-campo">Tipo de Servicio</label>
          <select
            id="tipo-servicio"
            className="input-select"
            value={tipoServicio}
            onChange={handleTipoServicioChange}
          >
            <option value="">Selecciona el tipo de servicio</option>
            <option value="recoger">Recoger en tienda</option>
            <option value="domicilio">Envío a Domicilio</option>
          </select>
        </div>

        {/* Campo de dirección, visible solo si el tipo de servicio es "domicilio" */}
        {tipoServicio === 'domicilio' && (
          <div className="campo-grupo">
            <label htmlFor="direccion-envio" className="etiqueta-campo">Dirección de Envío</label>
            <input
              type="text"
              id="direccion-envio"
              className="input-text"
              value={direccionEnvio}
              onChange={handleDireccionEnvioChange}
              placeholder="Ej: Calle Principal #123, Colonia, Ciudad"
            />
          </div>
        )}

        {/* Mostrar el tiempo estimado aquí, si se ha seleccionado un antojo o tipo de servicio */}
        {(selectedAntojo || tipoServicio) && tiempoEstimado && (
          <p className="tiempo-estimado">Tiempo de preparación y/o envío estimado: **{tiempoEstimado}**</p>
        )}

        {/* Botón "Completar Pedido" */}
        <button
          className="boton-completar-pedido"
          onClick={handleCompletarPedido}
          disabled={isCompletarPedidoDisabled}
        >
          Completar Pedido
        </button>
      </div>

      <p className="info-telefono">También puedes hacer tu pedido por teléfono al (123) 456-7890</p>

      {/* Modal de confirmación de pedido */}
      {mostrarModal && pedidoActual && (
        <PedidoConfirmacion
          pedido={pedidoActual}
          onClose={() => setMostrarModal(false)}
          onPedirMas={handlePedirMas}
          onCancelar={handleCancelarPedido}
          onTerminarPedido={handleTerminarPedido}
        />
      )}
    </div>
  );
}

export default RealizarPedido;
