// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom'; // Importa para routing
import Inicio from './components/Inicio';
import SobreNosotros from './components/SobreNosotros';
import Productos from './components/Productos'; // Tu componente de productos
import Ubicacion from './components/Ubicacion'; // Tu componente de Ubicación

import './App.css'; // Si tus estilos globales están en App.css

function App() {
  return (
    <Router>
      {/* El contenedor principal de la aplicación */}
      <div className="app-container">

        {/* HEADER: Eliminado el contenido de la etiqueta <header> */}
        <div className="header-placeholder">
          <div className="logo">Kayak Snack</div>
          <nav>
            <ul>
              <li><Link to="/">Inicio</Link></li>
              <li><Link to="/sobre-nosotros">Sobre Nosotros</Link></li>
              <li><Link to="/productos">Productos</Link></li>
              <li><Link to="/ubicacion">Ubicación</Link></li>
              {/* Eliminado el enlace a Realizar Pedido por ahora */}
              {/* <li><Link to="/realizar-pedido">Realizar Pedido</Link></li> */}
            </ul>
          </nav>
        </div>


        {/* MAIN: Aquí se renderizarán los componentes de cada ruta */}
        <main>
          <Routes> {/* Define tus rutas aquí */}
            <Route path="/" element={<Inicio />} />
            <Route path="/sobre-nosotros" element={<SobreNosotros />} />
            <Route path="/productos" element={<Productos />} />
            <Route path="/ubicacion" element={<Ubicacion />} />
            {/* Eliminada la ruta a Realizar Pedido por ahora */}
            {/* <Route path="/realizar-pedido" element={<RealizarPedido />} /> */}
          </Routes>
        </main>

        {/* FOOTER: Eliminado el contenido de la etiqueta <footer> */}
        <div className="footer-placeholder">
          <p>© 2025 Kayak Snack. Todos los derechos reservados.</p>
        </div>

      </div>
    </Router>
  );
}

export default App;
