// src/App.js
import React from 'react';
import './App.css'; // Importa tus estilos globales

// Suponiendo que tienes componentes para Header, MainContent (o tus secciones directas), y Footer
// Si no tienes estos componentes separados, imagina que el JSX de Header, Main y Footer
// está directamente aquí.

// Importa tu componente Ubicacion
import Ubicacion from './components/Ubicacion';
// Importa otros componentes de sección si los tienes
// import Productos from './components/Productos';
// import Contacto from './components/Contacto';


function App() {
  return (
    // Este div es el que tiene el fondo de borde a borde y centra el contenido.
    // YA NO USAMOS .app-container AQUÍ
    <div className="full-screen-wrapper"> {/* Nuevo div para envolver todo y manejar el fondo */}

      {/* El Header SIEMPRE va de borde a borde */}
      <header> {/* Asegúrate de que tu componente Header sea solo <header> o un div con esa clase */}
        <div className="header-content-container"> {/* Este div dentro del header SÍ LIMITA EL ANCHO */}
          <a href="/" className="logo">Kayak Snack</a>
          <nav>
            <ul>
              <li><button>Inicio</button></li>
              <li><button>Sobre Nosotros</button></li>
              <li><button>Productos</button></li>
              <li><button>Ubicación</button></li>
              <li><button>Realizar Pedido</button></li>
            </ul>
          </nav>
        </div>
      </header>

      {/* El Main es el que contendrá el contenido que sí tiene un ancho limitado y centrado */}
      <main className="main-content-wrapper"> {/* Clase para el wrapper del contenido principal */}
        {/* Aquí renderizas tus secciones/componentes */}
        <Ubicacion />
        {/* <Productos /> */}
        {/* <Contacto /> */}
      </main>

      {/* El Footer SIEMPRE va de borde a borde */}
      <footer> {/* Asegúrate de que tu componente Footer sea solo <footer> o un div con esa clase */}
        <div className="footer-content-container"> {/* Este div dentro del footer SÍ LIMITA EL ANCHO */}
          <p>&copy; 2025 Kayak Snack. Todos los derechos reservados.</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
