// src/components/Productos.js
import React, { useState } from 'react';
import './Productos.css';
import InfoProductos from './InfoProductos';

function Productos() {
  // Lista completa de productos con sus nombres y ruta de imagen
  const allProducts = [
    { name: 'Russa', image: '/images/russa.jpg', class: '' },
    { name: 'Fruta', image: '/images/frutas.jpeg', class: '' },
    { name: 'Elote en vaso', image: '/images/elote.jpeg', class: 'elote-imagen' },
    { name: 'Tosticeviche', image: '/images/tosticeviche.jpeg', class: 'tosticeviche-imagen' },
    { name: 'Tostielote', image: '/images/tostielote.jpeg', class: 'tostielote-imagen' },
    { name: 'Boyas', image: '/images/boyas.jpeg', class: 'boyas-imagen' },
    // ¡NUEVOS PRODUCTOS AÑADIDOS!
    { name: 'Chilidogs', image: '/images/chilidogs.jpeg', class: '' },
    { name: 'Litro Ceviche', image: '/images/litroceviche.jpeg', class: '' }, // Ajusta el nombre si quieres 'Litroceviche'
    { name: 'Chilidog', image: '/images/chilidog.jpeg', class: '' }, // Asumo que chilidog es individual
  ];

  // Estado para controlar cuántos productos se muestran inicialmente
  const [productosMostrados, setProductosMostrados] = useState(6); // Muestra los primeros 6 al inicio

  // Estado para controlar qué producto se muestra en el modal (null si ninguno)
  const [selectedProduct, setSelectedProduct] = useState(null);

  // Función para abrir el modal con la información del producto
  const handleCardClick = (productName) => {
    setSelectedProduct({ nombre: productName });
  };

  // Función para cerrar el modal
  const handleCloseModal = () => {
    setSelectedProduct(null);
  };

  // Función para mostrar más productos
  const handleVerMas = () => {
    // Muestra 3 productos adicionales cada vez, o todos si quedan menos de 3
    setProductosMostrados(prevCount => prevCount + 3);
  };

  return (
    <div className="seccion-productos">
      <h1 className="titulo-seccion-productos">Nuestros Productos</h1>
      <hr className="linea-decorativa-amarilla" />

      <div className="galeria-productos">
        {/* Usamos map para renderizar los productos basados en el estado */}
        {allProducts.slice(0, productosMostrados).map((product, index) => (
          <div
            className="tarjeta-producto"
            key={index} // Usa un ID único si tienes, si no, el índice está bien para listas estáticas
            onClick={() => handleCardClick(product.name)}
          >
            <img
              src={product.image}
              alt={product.name + " Kayak Snack"}
              className={`imagen-producto ${product.class}`} // Aplica clases específicas si las hay
            />
            <h3 className="nombre-producto">{product.name}</h3>
          </div>
        ))}
      </div>

      {/* Solo muestra el botón "Ver más" si aún quedan productos por mostrar */}
      {productosMostrados < allProducts.length && (
        <div className="contenedor-boton-productos">
          <button onClick={handleVerMas} className="boton-explorar-productos">
            Ver más &rarr;
          </button>
        </div>
      )}

      {/* Renderiza el modal si hay un producto seleccionado */}
      <InfoProductos producto={selectedProduct} onClose={handleCloseModal} />
    </div>
  );
}

export default Productos;
